            let obj = Object.values(data);
            obj.splice(5);
            setImages(obj)
            console.log("images "+images)
    });
    },[])

    handleClick((i)=>{
        setSingle(images[i]);
    })

    return(
        <div>
            <img src={single}/>
            <h1>Image List Component</h1>
                {images.map((image,i)=>(
                    <>
                        <img src={image.thumbnailUrl} key={i} onClick={handleClick}/>
                    </>
                ))}
        </div>
    )

}

